function clickOnBrowseButton(){document.querySelector(".search-form__browse").addEventListener("click",(()=>{document.querySelector(".search-form__browse").nextElementSibling.classList.contains("drop__down")?document.querySelector(".search-form__browse").nextElementSibling.classList.toggle("drop__down"):document.querySelector(".search-form__browse").nextElementSibling.classList.add("drop__down")}))}function makeActiveNavItem(){let e=document.querySelector("[data-id-page]").getAttribute("data-id-page"),t=document.querySelector(`[data-id-nav=${e}]`);e===t.getAttribute("data-id-nav")&&t.classList.add("menu__list_active")}clickOnBrowseButton(),makeActiveNavItem();